// รับ input จาก node ก่อนหน้า
const input = items[0].json;

// ดึงสตริงจากฟิลด์ output
let raw = input.output;

// เผื่อกรณี output ไม่ใช่สตริง (แต่โดยรูปนี้เป็นสตริง)
if (typeof raw !== 'string') {
  raw = JSON.stringify(raw);
}

// ตัดส่วนเกริ่นนำออก แล้วเก็บเฉพาะ JSON object ระหว่าง { ... }
const start = raw.indexOf('{');
const end = raw.lastIndexOf('}');
if (start === -1 || end === -1) {
  throw new Error('ไม่พบ JSON object ในข้อความ output');
}
const jsonString = raw.slice(start, end + 1);

// แปลงเป็นออบเจกต์
const data = JSON.parse(jsonString);

// ป้องกันกรณีไม่มี pages หรือ posts
const pages = Array.isArray(data.pages) ? data.pages : [];
const flattened = [];

// ไส้ flatten
for (const page of pages) {
  const posts = Array.isArray(page.posts) ? page.posts : [];
  for (const post of posts) {
    flattened.push({
      page_name: page.page_name ?? null,
      post_time: post.post_time ?? null,
      text: post.text ?? "",
      likes: post.likes ?? 0,
      comments_count: post.comments_count ?? 0,
      shares: post.shares ?? 0,
      media_type: post.media_type ?? null,
      total_likes: page.total_likes ?? 0,
      total_comments: page.total_comments ?? 0,
      total_shares: page.total_shares ?? 0,
      total_engagement: page.total_engagement ?? 0,
      average_engagement: page.average_engagement ?? 0,
      // ถ้าอยากพก insights / recommendations มาทุกแถวด้วย ใส่ 2 บรรทัดนี้:
      insights: Array.isArray(data.insights) ? data.insights : [],
      recommendations: Array.isArray(data.recommendations) ? data.recommendations : [],
    });
  }
}

// ส่งออกเป็น n8n items
return flattened.map(row => ({ json: row }));

// Code node สำหรับสุ่มเลือกคำถาม
const questions = [
  "ดึงโพส https://www.facebook.com/nationtv และ https://www.facebook.com/thaipbs มาเปรียบเทียบกัน 1 โพสล่าสุด พร้อม comment 5 comment",
  "วิเคราะห์ engagement ของ https://www.facebook.com/natenapa.nate โพส 5 โพสล่าสุด",
  "ตรวจสอบ sentiment ของคอมเมนต์ใน https://www.facebook.com/imcinstitute โพส 3 โพสล่าสุด",
  "เปรียบเทียบ engagement ระหว่าง https://www.facebook.com/natenapa.nate และ https://www.facebook.com/Lost.Somewhere.Together ภายใต้ 5 โพสล่าสุด"
];

// สุ่มเลือกคำถาม หรือใช้แบบเรียงตามลำดับ
const currentHour = new Date().getHours();
const questionIndex = currentHour % questions.length; // หมุนเวียนตามชั่วโมง
const selectedQuestion = questions[questionIndex];

// สร้าง session ID แบบ unique
const sessionId = `facebook-analysis-${new Date().toISOString().split('T')[0]}-${questionIndex}`;

// ส่งข้อมูลไปยัง Agent
return [{
  json: {
    chatInput: selectedQuestion,
    sessionId: sessionId, // เพิ่ม sessionId ตรงนี้
    timestamp: new Date().toISOString(),
    questionIndex: questionIndex,
    executionType: "scheduled"
  }
}];
